mapper.select:
select c.*,a.author_name from abjy_tb_comment c, (select * from (SELECT teacher_id as user_id ,teacher_name as `author_name` FROM abjy_tb_teacher union SELECT pa.parent_id as user_id, if(pa.parent_id = #{myId},pa.parent_name, concat(ch.child_name,cc.relation_name)) as author_name FROM abjy_tb_parent pa, abjy_tb_child ch, abjy_tb_child_contact cc WHERE cc.child_id = ch.child_id and ch.child_status in (1,2) and cc.is_delete = 0 and ch.is_delete = 0 AND pa.parent_id = cc.parent_id
AND ch.classes_id = #{classId}
UNION SELECT pa.parent_id, if(pa.parent_id = #{myId},pa.parent_name, concat(ch.child_name,ch.relation_name)) as parent_name FROM abjy_tb_parent pa, abjy_tb_child ch WHERE ch.parent_id = pa.parent_id and ch.is_delete = 0 and ch.child_status in (1,2)
AND ch.classes_id = #{classId}
)b group by user_id ) a where `c`.upper_id = #{id} and `c`.comment_author_id = a.user_id
and (`c`.is_hide = 0 or c.comment_author_id =#{myId})
and `c`.is_delete = 0

select a.* from abjy_tb_like c, (select * from ((SELECT teacher_id as like_user_id ,teacher_name as `author_name` FROM abjy_tb_teacher) union SELECT pa.parent_id as like_user_id, if(pa.parent_id = #{myId},pa.parent_name, concat(ch.child_name,cc.relation_name)) as author_name FROM abjy_tb_parent pa, abjy_tb_child ch, abjy_tb_child_contact cc WHERE cc.child_id = ch.child_id and ch.child_status in (1,2) and cc.is_delete = 0 and ch.is_delete = 0 AND pa.parent_id = cc.parent_id
AND ch.classes_id = #{classId}
UNION SELECT pa.parent_id, if(pa.parent_id = #{myId},pa.parent_name, concat(ch.child_name,ch.relation_name)) as parent_name FROM abjy_tb_parent pa, abjy_tb_child ch WHERE ch.parent_id = pa.parent_id and ch.child_status in (1,2) and ch.is_delete = 0
AND ch.classes_id = #{classId}
)b group by like_user_id ) a where c.like_user_id = a.like_user_id and c.moment_id = #{momentId}

SELECT * FROM abjy_tb_moment m, (select * from (SELECT teacher_id as user_id ,teacher_name as `author_name`,teacher_avatar as author_avatar FROM abjy_tb_teacher union SELECT pa.parent_id as user_id, concat(ch.child_name,cc.relation_name) as author_name,pa.parent_avatar FROM abjy_tb_parent pa, abjy_tb_child ch, abjy_tb_child_contact cc WHERE cc.child_id = ch.child_id AND pa.parent_id = cc.parent_id UNION SELECT pa.parent_id, concat(ch.child_name,ch.relation_name) as parent_name,pa.parent_avatar FROM abjy_tb_parent pa, abjy_tb_child ch WHERE ch.parent_id = pa.parent_id )b group by user_id ) a WHERE m.moment_author_id = a.user_id
AND (m.is_hide = 0 or m.moment_author_id =#{myId})
AND m.is_delete = 0 and m.moment_id = #{momentId}
